mapper.select:
select
id as id,
orderNo as orderNo,
userId as userId,
schedulerId as schedulerId,
itemId as itemId,
itemName as itemName,
wxTradeNo as wxTradeNo,
aliTradeNo as aliTradeNo,
orderType as orderType,
payType as payType,
totalCount as totalCount,
sourceType as sourceType,
isNeedInvoice as isNeedInvoice,
invoiceType as invoiceType,
invoiceHead as invoiceHead,
invoiceNo as invoiceNo,
isNeedInsurance as isNeedInsurance,
totalAmount as totalAmount,
insuranceAmount as insuranceAmount,
createdTime as createdTime,
updatedTime as updatedTime
from dm_order


        and id=#{id}


        and orderNo=#{orderNo}


        and userId=#{userId}


        and schedulerId=#{schedulerId}


        and itemId=#{itemId}


        and itemName=#{itemName}


        and wxTradeNo=#{wxTradeNo}


        and aliTradeNo=#{aliTradeNo}


        and orderType=#{orderType}


        and payType=#{payType}


        and totalCount=#{totalCount}


        and sourceType=#{sourceType}


        and isNeedInvoice=#{isNeedInvoice}


        and invoiceType=#{invoiceType}


        and invoiceHead=#{invoiceHead}


        and invoiceNo=#{invoiceNo}


        and isNeedInsurance=#{isNeedInsurance}


        and totalAmount=#{totalAmount}


        and insuranceAmount=#{insuranceAmount}


        and createdTime=#{createdTime}


        and updatedTime=#{updatedTime}



    limit #{beginPos},#{pageSize}

select count(*) from dm_order


        and id=#{id}


        and orderNo=#{orderNo}


        and userId=#{userId}


        and schedulerId=#{schedulerId}


        and itemId=#{itemId}


        and itemName=#{itemName}


        and wxTradeNo=#{wxTradeNo}


        and aliTradeNo=#{aliTradeNo}


        and orderType=#{orderType}


        and payType=#{payType}


        and totalCount=#{totalCount}


        and sourceType=#{sourceType}


        and isNeedInvoice=#{isNeedInvoice}


        and invoiceType=#{invoiceType}


        and invoiceHead=#{invoiceHead}


        and invoiceNo=#{invoiceNo}


        and isNeedInsurance=#{isNeedInsurance}


        and totalAmount=#{totalAmount}


        and insuranceAmount=#{insuranceAmount}


        and createdTime=#{createdTime}


        and updatedTime=#{updatedTime}

select id as id, orderNo as orderNo, userId as userId, schedulerId as schedulerId, itemId as itemId, itemName as itemName, wxTradeNo as wxTradeNo, aliTradeNo as aliTradeNo, orderType as orderType, payType as payType, totalCount as totalCount, sourceType as sourceType, isNeedInvoice as isNeedInvoice, invoiceType as invoiceType, invoiceHead as invoiceHead, invoiceNo as invoiceNo, isNeedInsurance as isNeedInsurance, totalAmount as totalAmount, insuranceAmount as insuranceAmount, createdTime as createdTime, updatedTime as updatedTime from dm_order
and createdTime >= DATE_ADD(NOW(), INTERVAL - 1 YEAR)
and createdTime >= DATE_ADD(NOW(), INTERVAL - 3 MONTH)
and userId=#{userId}
and orderType=#{orderType}
and ( orderNo LIKE #{orderNo}
OR itemName LIKE #{itemName} )
limit #{beginPos},#{pageSize}
