mapper.select:
select
id as id,
x as x,
y as y,
areaLevel as areaLevel,
scheduleId as scheduleId,
orderNo as orderNo,
userId as userId,
status as status,
sort as sort,
createdTime as createdTime,
updatedTime as updatedTime
from dm_scheduler_seat


        and id=#{id}


        and x=#{x}


        and y=#{y}


        and areaLevel=#{areaLevel}


        and orderNo=#{orderNo}


        and scheduleId=#{scheduleId}


        and orderNo=#{orderNo}


        and userId=#{userId}


        and status=#{status}


        and sort=#{sort}


        and createdTime=#{createdTime}


        and updatedTime=#{updatedTime}



    limit #{beginPos},#{pageSize}

select count(*) from dm_scheduler_seat


        and id=#{id}


        and x=#{x}


        and y=#{y}


        and areaLevel=#{areaLevel}


        and scheduleId=#{scheduleId}


        and userId=#{userId}


        and status=#{status}


        and sort=#{sort}


        and createdTime=#{createdTime}


        and updatedTime=#{updatedTime}

select id as id, x as x, y as y, areaLevel as areaLevel, scheduleId as scheduleId, orderNo as orderNo, userId as userId, status as status, sort as sort, createdTime as createdTime, updatedTime as updatedTime from dm_scheduler_seat
and scheduleId=#{scheduleId}
and x=#{x}
and y=#{y}
